import React from 'react';
import { useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import Paper from '@material-ui/core/Paper';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import TableFooter from '@material-ui/core/TableFooter';
import moment from 'moment';
import 'date-fns';

const useStyles = makeStyles((theme) => ({
    rootTable: {

      },
    containerTable: {
        MuiTableCell: {
            tfoot: {
                left: 0,
                bottom: 0, // <-- KEY
                zIndex: 2,
                position: 'sticky'
            }
        }
    },
}));

const columns = [
    { 
      id: 'bpm', 
      label: 'Beats per Minute',
      align: 'center' 
      },
    {
      id: 'date',
      label: 'Date/Time Recorded',
      minWidth: 170,
      align: 'center',
      format: (value) => moment(value).day(),
    },
];

const TableComponent = (props) => {
    const classes = useStyles();

    useEffect(() => {
        document.getElementById("tableFooter").style.position = 'sticky';
    }, [])

    return (
        <Paper className={classes.rootTable} style={{height: '71vh'}}>
            <TableContainer className={classes.containerTable} style={{height: '100%'}}>
                <Table stickyHeader stickyFooter aria-label="sticky table">
                <TableHead>
                    <TableRow>
                    {columns.map((column) => (
                        <TableCell
                        key={column.id}
                        align={column.align}
                        style={{ minWidth: column.minWidth }}
                        >
                        {column.label}
                        </TableCell>
                    ))}
                    </TableRow>
                </TableHead>
                    <TableBody id="currentRows">
                        {(props.rowsPerPage > 0
                            ? props.rows.slice(props.page * props.rowsPerPage, props.page * props.rowsPerPage + props.rowsPerPage)
                            : props.rows).map((row) => {
                        return (
                            <TableRow hover role="checkbox" tabIndex={-1} key={row.unNum}>
                            {columns.map((column) => {
                                const value = row[column.id];
                                return (
                                <TableCell key={column.id} align={column.align}>
                                    {column.format && typeof value === 'number' ? column.format(value) : value}
                                </TableCell>
                                );
                            })}
                            </TableRow>
                        );
                        })}
                    </TableBody>
                </Table>
                <TableFooter id="tableFooter">
                    <TablePagination
                        rowsPerPageOptions={[10, 25, 100, { label: 'All', value: -1 }]}
                        component="div"
                        count={props.rows.length}
                        rowsPerPage={props.rowsPerPage > 0 ? props.rowsPerPage : props.rows.length}
                        page={props.page}
                        onChangePage={props.handleChangePage}
                        onChangeRowsPerPage={props.handleChangeRowsPerPage}
                    />
                </TableFooter>
            </TableContainer>
        </Paper>
    )
}

export default TableComponent
